import React, { Component } from "react";

class Education extends Component {
  constructor(props) {
    super(props);
    this.state = {
      edit: this.props.edit,

      inputs: {
        firstName: "name",
        lastName: "",
        email: "",
        phone: "",
      },
    };
    this.handler = this.handler.bind(this);
    this.updateInputState = this.updateInputState.bind(this);
    this.createInputs = this.createInputs.bind(this);
    this.buttonHandler = this.buttonHandler.bind(this);
  }

  handler(e) {
    this.updateInputState(e);
    this.props.updateEducation(this.props.educationID, this.state.inputs);
  }

  updateInputState(e) {
    this.setState((prevState) => ({
      inputs: { ...prevState.inputs, [e.target.id]: e.target.value },
    }));
  }

  buttonHandler(e) {
    e.preventDefault();

    this.setState((prevState) => ({
      edit: !prevState.edit,
    }));
    this.props.addForm();
  }

  createInputs(state) {
    let ids = ["firstName", "lastName", "email", "phone"];
    if (state) {
      return ids.map((id) => {
        return (
          <input
            id={id}
            onChange={this.handler}
            value={this.state.inputs[id]}
          ></input>
        );
      });
    } else {
      return ids.map((id) => {
        return <p>{this.state.inputs[id]}</p>;
      });
    }
  }

  createButton() {
    if (this.state.edit) {
      return <button onClick={this.buttonHandler}>Save</button>;
    } else {
      return <button onClick={this.buttonHandler}>Edit</button>;
    }
  }

  render() {
    return (
      <div>
        <form className="form">
          <h2>education</h2>
          <label for="firstName">First Name:</label>
          {this.createInputs(this.state.edit)[0]}
          <label for="lastName">Last Name:</label>
          {this.createInputs(this.state.edit)[1]}
          <label for="email">Email:</label>
          {this.createInputs(this.state.edit)[2]}
          <label for="phone">Phone:</label>
          {this.createInputs(this.state.edit)[3]}
          {this.createButton()}
        </form>
      </div>
    );
  }
}

export default Education;
