import React, { Component } from "react";
import Education from "./education";

class EducationsHandler extends Component {
  constructor(props) {
    super(props);

    this.state = {
      educations: 1,
    };
    this.createForms = this.createForms.bind(this);
    this.addForm = this.addForm.bind(this);
    this.updateEducationHandler = this.updateEducationHandler.bind(this);
  }

  updateEducationHandler(index, educationObj) {
    this.props.updateEducation(index, educationObj);
  }

  createForms() {
    let formsArray = [];
    var i;
    for (i = 1; i < this.state.educations; i++) {
      formsArray = [
        ...formsArray,
        <Education
          addForm={this.addForm}
          cvData={this.props.cvData}
          updateEducation={this.updateEducationHandler}
          edit={false}
          educationID={i - 1}
        ></Education>,
      ];
    }
    formsArray = [
      ...formsArray,
      <Education
        addForm={this.addForm}
        cvData={this.props.cvData}
        updateEducation={this.updateEducationHandler}
        edit="true"
        educationID={this.state.educations - 1}
      ></Education>,
    ];
    return formsArray;
  }

  addForm() {
    this.setState((prevState) => ({
      educations: prevState.educations + 1,
    }));
  }

  deleteForm() {
    this.setState((prevState) => ({
      educations: prevState.educations - 1,
    }));
  }

  render() {
    return <div>{this.createForms()}</div>;
  }
}

export default EducationsHandler;
