import React, { Component } from "react";

class Education extends Component {
  constructor(props) {
    super(props);
    this.state = {
      edit: this.props.edit,

      inputs: {
        firstName: "test",
        lastName: "",
        email: "",
        phone: "",
      },
    };
    this.handler = this.handler.bind(this);
    this.updateInputState = this.updateInputState.bind(this);
    this.createInputs = this.createInputs.bind(this);
    this.buttonHandler = this.buttonHandler.bind(this);
  }

  handler(e) {
    this.updateInputState(e);
  }

  updateInputState(e) {
    this.setState((prevState) => ({
      inputs: { ...prevState.inputs, [e.target.id]: e.target.value },
    }));
  }

  buttonHandler(e) {
    e.preventDefault();

    this.setState((prevState) => ({
      edit: !prevState.edit,
    }));

    if (this.state.edit) {
      this.setState({
        edit: false,
      });
      this.props.addForm();
    } else {
      this.setState({
        edit: true,
      });
      this.props.deleteForm();
    }
  }

  createInputs(state) {
    let ids = ["firstName", "lastName", "email", "phone"];
    if (state) {
      return ids.map((id) => {
        return (
          <input
            id={id}
            onChange={this.handler}
            value={this.state.inputs[id]}
          ></input>
        );
      });
    } else {
      return ids.map((id) => {
        return <p>{this.state.inputs[id]}</p>;
      });
    }
  }

  createButton() {
    if (this.state.edit) {
      return <button onClick={this.buttonHandler}>Add</button>;
    } else {
      return <button onClick={this.buttonHandler}>Edit</button>;
    }
  }

  render() {
    return (
      <div>
        <form className="form">
          <h2>education</h2>
          <label htmlFor="firstName">First Name:</label>
          {this.createInputs(this.state.edit)[0]}
          <label htmlFor="lastName">Last Name:</label>
          {this.createInputs(this.state.edit)[1]}
          <label htmlFor="email">Email:</label>
          {this.createInputs(this.state.edit)[2]}
          <label htmlFor="phone">Phone:</label>
          {this.createInputs(this.state.edit)[3]}
          {this.createButton()}
        </form>
      </div>
    );
  }
}

export default Education;
