import "./App.css";
import Personal from "./components/personal";
import EducationsHandler from "./components/educationsHandler";
import Expereience from "./components/experience";
import Preview from "./components/preview";
import React, { Component } from "react";

class App extends Component {
  constructor() {
    super();

    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      education: [{ firstName: "balls" }, { firstName: "balls2" }],
    };
    this.updatePerson = this.updatePerson.bind(this);
    this.updateEducation = this.updateEducation.bind(this);
  }

  updatePerson(stateName, value) {
    this.setState((prevState) => ({
      [stateName]: value,
    }));
  }

  updateEducation(index, educationObj) {
    console.log("update education");
    let educationCopy = [...this.state.education];
    let newEducation = educationObj;
    educationCopy[index] = newEducation;
    this.setState((prevState) => ({
      education: educationCopy,
    }));
  }

  render() {
    return (
      <div id="app">
        <Personal updateCV={this.updatePerson}></Personal>
        <EducationsHandler
          updateEducation={this.updateEducation}
          cvData={this.state}
        ></EducationsHandler>
        <Expereience></Expereience>
        <Preview cvData={this.state}></Preview>
        <div>{this.state.education[0].firstName}</div>
      </div>
    );
  }
}

export default App;
