import "./App.css";
import Personal from "./components/personal";
import EducationsHandler from "./components/educationsHandler";
import Expereience from "./components/experience";
import Preview from "./components/preview";
import React, { Component } from "react";

class App extends Component {
  constructor() {
    super();

    this.state = {
      cv: {
        personal: {
          firstName: "",
          lastName: "",
        },
        education: [],
      },
    };
    this.updateCV = this.updateCV.bind(this);
  }

  updateCV(stateName, value) {
    this.setState((prevState) => ({
      cv: { ...prevState.cv, [stateName]: value },
    }));
  }

  render() {
    return (
      <div id="app">
        <Personal updateCV={this.updateCV}></Personal>
        <EducationsHandler></EducationsHandler>
        <Expereience></Expereience>
        <Preview cvData={this.state.cv}></Preview>
      </div>
    );
  }
}

export default App;
