import React, { Component } from "react";
import "./formStyles.css";

class Personal extends Component {
  constructor(props) {
    super(props);
    this.state({
      inputs: {
        firstName: "",
        lastName: "",
        email: "",
        phone: "",
      },
    });
    this.handler = this.handler.bind(this);
    this.updateInputState = this.updateInputState.bind(this);
  }

  handler(e) {
    this.updateInputState(e);
    this.props.updateCV(e.target.id, e.target.value);
  }

  updateInputState(e) {
    this.setState((prevState) => ({
      inputs: { ...prevState.inputs, [e.target.id]: e.target.value },
    }));
  }

  render() {
    return (
      <div>
        <form className="form">
          <label for="firstName">First Name:</label>
          <input id="firstName" onChange={this.handler}></input>
          <label for="lastName">Last Name:</label>
          <input id="lastName" onChange={this.handler}></input>
          <label for="email">Email:</label>
          <input id="email" onChange={this.handler}></input>
          <label for="phone">Phone:</label>
          <input id="phone" onChange={this.handler}></input>
          <button>Save</button>
        </form>
      </div>
    );
  }
}

export default Personal;
