import React, { Component } from "react";
import "./formStyles.css";

class Personal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      edit: true,
      inputs: {
        firstName: "name",
        lastName: "",
        email: "",
        phone: "",
      },
    };
    this.handler = this.handler.bind(this);
    this.updateInputState = this.updateInputState.bind(this);
    this.createInputs = this.createInputs.bind(this);
    this.save = this.save.bind(this);
  }

  handler(e) {
    this.updateInputState(e);
    this.props.updateCV(e.target.id, e.target.value);
  }

  updateInputState(e) {
    this.setState((prevState) => ({
      inputs: { ...prevState.inputs, [e.target.id]: e.target.value },
    }));
  }

  save(e) {
    e.preventDefault();
    this.setState({
      edit: false,
    });
  }

  createInputs(state) {
    let ids = ["firstName", "lastName", "email", "phone"];
    if (state) {
      return ids.map((id) => {
        return (
          <input
            id={id}
            onChange={this.handler}
            value={this.state.inputs[id]}
          ></input>
        );
      });
    } else {
      return ids.map((id) => {
        return <p>{this.state.inputs[id]}</p>;
      });
    }
  }

  render() {
    return (
      <div>
        <form className="form">
          <label for="firstName">First Name:</label>
          {this.createInputs(this.state.edit)[0]}
          <label for="lastName">Last Name:</label>
          {this.createInputs(this.state.edit)[1]}
          <label for="email">Email:</label>
          {this.createInputs(this.state.edit)[2]}
          <label for="phone">Phone:</label>
          {this.createInputs(this.state.edit)[3]}
          <button onClick={this.save}>Save</button>
        </form>
      </div>
    );
  }
}

export default Personal;
