import React, { Component } from "react";
import Experience from "./experience";

class ExperienceHandler extends Component {
  constructor(props) {
    super(props);

    this.state = {
      educations: 1,
    };
    this.createForms = this.createForms.bind(this);
    this.addForm = this.addForm.bind(this);
    this.updateEducationHandler = this.updateEducationHandler.bind(this);
    this.deleteForm = this.deleteForm.bind(this);
  }

  createForms() {
    let formsArray = [];
    var i;
    for (i = 1; i < this.state.educations; i++) {
      formsArray = [
        ...formsArray,
        <Experience
          key={i}
          addForm={this.addForm}
          deleteForm={this.deleteForm}
          cvData={this.props.cvData}
          updateEducation={this.updateEducationHandler}
          edit={false}
          educationID={i - 1}
        ></Experience>,
      ];
    }
    formsArray = [
      ...formsArray,
      <Experience
        key={this.state.educations - 1}
        addForm={this.addForm}
        deleteForm={this.deleteForm}
        cvData={this.props.cvData}
        updateEducation={this.props.updateEducation}
        edit="true"
        educationID={this.state.educations - 1}
      ></Experience>,
    ];
    return formsArray;
  }

  addForm() {
    this.setState((prevState) => ({
      educations: prevState.educations + 1,
    }));
  }

  deleteForm() {
    this.setState((prevState) => ({
      educations: prevState.educations - 1,
    }));
  }

  render() {
    return <div>{this.createForms()}</div>;
  }
}

export default ExperienceHandler;
