import "./App.css";
import Personal from "./components/personal";
import Education from "./components/education";
import Expereience from "./components/experience";
import Preview from "./components/preview";
import React, { Component } from "react";

class App extends Component {
  constructor() {
    super();

    this.state = {
      cv: { firstName: "tefdgst" },
    };
    this.updateCV = this.updateCV.bind(this);
  }

  updateCV(e) {
    console.log("change");
    this.setState((prevState) => ({
      cv: { ...prevState.cv, firstName: e.target.value },
    }));
  }

  render() {
    return (
      <div id="app">
        <Personal updateCV={this.updateCV}></Personal>
        <Education></Education>
        <Expereience></Expereience>
        <Preview cvData={this.state.cv}></Preview>
      </div>
    );
  }
}

export default App;
